import * as React from "react";
import GlobalStyles from "@mui/joy/GlobalStyles";
import Avatar from "@mui/joy/Avatar";
import Box from "@mui/joy/Box";
import Divider from "@mui/joy/Divider";
import IconButton from "@mui/joy/IconButton";
import Input from "@mui/joy/Input";
import List from "@mui/joy/List";
import ListItem from "@mui/joy/ListItem";
import ListItemButton, { listItemButtonClasses } from "@mui/joy/ListItemButton";
import ListItemContent from "@mui/joy/ListItemContent";
import Typography from "@mui/joy/Typography";
import Sheet from "@mui/joy/Sheet";
import SearchRoundedIcon from "@mui/icons-material/SearchRounded";
import AssignmentRoundedIcon from "@mui/icons-material/AssignmentRounded";
import LogoutRoundedIcon from "@mui/icons-material/LogoutRounded";
import BrightnessAutoRoundedIcon from "@mui/icons-material/BrightnessAutoRounded";
import AddRoundedIcon from "@mui/icons-material/AddRounded";

import ColorSchemeToggle from "./ColorSchemeToggle";
import { closeSidebar } from "../lib/utils";
import { useAuth } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import {
  Modal,
  ModalDialog,
  ModalClose,
  FormControl,
  FormLabel,
  Button,
  Select,
  Option,
  Textarea,
} from "@mui/joy";
import { createTask } from "../api/task";

export default function Sidebar() {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  const [open, setOpen] = React.useState(false);
  const [title, setTitle] = React.useState("");
  const [description, setDescription] = React.useState("");
  const [dueDate, setDueDate] = React.useState(""); // <-- новое поле
  const [status, setStatus] = React.useState<
    "NEW" | "IN_PROGRESS" | "COMPLETED"
  >("NEW");

  const handleCreate = async () => {
    if (!title.trim()) {
      alert("Введите название задачи");
      return;
    }

    try {
      await createTask({ title, description, status, dueDate }); // <-- передаём dueDate
      alert("Задача создана");
      setOpen(false);
      setTitle("");
      setDescription("");
      setDueDate(""); // сброс
      setStatus("NEW");
    } catch (err) {
      console.error(err);
      alert("Ошибка при создании задачи");
    }

    return (
      <Sheet
        className="Sidebar"
        sx={{
          position: { xs: "fixed", md: "sticky" },
          transform: {
            xs: "translateX(calc(100% * (var(--SideNavigation-slideIn, 0) - 1)))",
            md: "none",
          },
          transition: "transform 0.4s, width 0.4s",
          zIndex: 10000,
          height: "100dvh",
          width: "var(--Sidebar-width)",
          top: 0,
          p: 2,
          flexShrink: 0,
          display: "flex",
          flexDirection: "column",
          gap: 2,
          borderRight: "1px solid",
          borderColor: "divider",
        }}
      >
        <GlobalStyles
          styles={(theme) => ({
            ":root": {
              "--Sidebar-width": "220px",
              [theme.breakpoints.up("lg")]: {
                "--Sidebar-width": "240px",
              },
            },
          })}
        />
        <Box
          className="Sidebar-overlay"
          sx={{
            position: "fixed",
            zIndex: 9998,
            top: 0,
            left: 0,
            width: "100vw",
            height: "100vh",
            opacity: "var(--SideNavigation-slideIn)",
            backgroundColor: "var(--joy-palette-background-backdrop)",
            transition: "opacity 0.4s",
            transform: {
              xs: "translateX(calc(100% * (var(--SideNavigation-slideIn, 0) - 1) + var(--SideNavigation-slideIn, 0) * var(--Sidebar-width, 0px)))",
              lg: "translateX(-100%)",
            },
          }}
          onClick={() => closeSidebar()}
        />
        <Box sx={{ display: "flex", gap: 1, alignItems: "center" }}>
          <IconButton variant="soft" color="primary" size="sm">
            <BrightnessAutoRoundedIcon />
          </IconButton>
          <Typography level="title-lg">TaskBoard</Typography>
          <ColorSchemeToggle sx={{ ml: "auto" }} />
        </Box>
        <Input
          size="sm"
          startDecorator={<SearchRoundedIcon />}
          placeholder="Search"
        />
        <Button
          startDecorator={<AddRoundedIcon />}
          size="sm"
          fullWidth
          onClick={() => setOpen(true)}
        >
          Новая задача
        </Button>

        <Box
          sx={{
            minHeight: 0,
            overflow: "hidden auto",
            flexGrow: 1,
            display: "flex",
            flexDirection: "column",
            [`& .${listItemButtonClasses.root}`]: {
              gap: 1.5,
            },
          }}
        >
          <List
            size="sm"
            sx={{
              gap: 1,
              "--List-nestedInsetStart": "30px",
              "--ListItem-radius": (theme) => theme.vars.radius.sm,
            }}
          >
            <ListItem>
              <ListItemButton selected>
                <AssignmentRoundedIcon />
                <ListItemContent>
                  <Typography level="title-sm">Tasks</Typography>
                </ListItemContent>
              </ListItemButton>
            </ListItem>
          </List>
        </Box>
        <Divider />
        <Box sx={{ display: "flex", gap: 1, alignItems: "center" }}>
          <Avatar variant="outlined" size="sm" />
          <Box sx={{ minWidth: 0, flex: 1 }}>
            <Typography level="title-sm">
              {user ? user.email.split("@")[0] : "Loading..."}
            </Typography>
            <Typography level="body-xs">
              {user ? user.email : "Loading..."}
            </Typography>
          </Box>
          <IconButton
            size="sm"
            variant="plain"
            color="neutral"
            onClick={() => {
              logout();
              navigate("/login");
            }}
          >
            <LogoutRoundedIcon />
          </IconButton>
        </Box>

        <Modal open={open} onClose={() => setOpen(false)}>
          <ModalDialog>
            <ModalClose />
            <Typography level="h4">Создать новую задачу</Typography>

            <FormControl>
              <FormLabel>Название</FormLabel>
              <Input value={title} onChange={(e) => setTitle(e.target.value)} />
            </FormControl>

            <FormControl>
              <FormLabel>Описание</FormLabel>
              <Textarea
                value={description}
                minRows={3}
                onChange={(e) => setDescription(e.target.value)}
              />
            </FormControl>

            <FormControl>
              <FormLabel>Срок выполнения</FormLabel>
              <Input
                type="date"
                value={dueDate}
                onChange={(e) => setDueDate(e.target.value)}
              />
            </FormControl>

            <FormControl>
              <FormLabel>Статус</FormLabel>
              <Select
                value={status}
                onChange={(_, value) => value && setStatus(value)}
              >
                <Option value="NEW">New</Option>
                <Option value="IN_PROGRESS">In progress</Option>
                <Option value="COMPLETED">Completed</Option>
              </Select>
            </FormControl>

            <Button onClick={handleCreate}>Создать</Button>
          </ModalDialog>
        </Modal>
      </Sheet>
    );
  };
}
