import { useCallback, useEffect, useState } from "react";
import { getTasks, deleteTask, TaskResponse } from "../api/task";
type RowType = {
  id: string;
  title: string; // ✅ Добавили title
  date: string;
  status: string;
  customer: {
    initial: string;
    name: string;
    email: string;
  };
};

export function useTasks() {
  const [rows, setRows] = useState<RowType[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<Error | null>(null);

  const loadTasks = useCallback(async () => {
    setLoading(true);
    try {
      const data: TaskResponse[] = await getTasks();
      const mapped: RowType[] = data.map((task) => ({
        id: String(task.id),
        title: task.title,
        date: new Date(task.createdAt).toLocaleDateString(),
        status: task.status[0] + task.status.substring(1).toLowerCase(),
        customer: {
          initial: "T",
          name: "Test User",
          email: "test@example.com",
        },
      }));
      setRows(mapped);
      setError(null);
    } catch (err) {
      console.error("Ошибка при загрузке задач", err);
      setError(err as Error);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    loadTasks();
  }, [loadTasks]);

  const handleDelete = async (id: string) => {
    try {
      await deleteTask(Number(id));
      await loadTasks();
    } catch (err) {
      console.error("Ошибка при удалении задачи", err);
      alert("Не удалось удалить задачу");
    }
  };

  return { rows, loading, error, refresh: loadTasks, handleDelete };
}
