import { Box, FormControl, FormLabel, Select, Option } from "@mui/joy";

export interface Filters {
  status?: "NEW" | "IN_PROGRESS" | "COMPLETED";
  sort?: "createdAt" | "title";
}

interface TaskFiltersProps {
  filters: Filters;
  onFilterChange: (filters: Filters) => void;
}

export default function TaskFilters({ filters, onFilterChange }: TaskFiltersProps) {
  return (
    <Box
      sx={{
        borderRadius: "sm",
        py: 2,
        display: { xs: "none", sm: "flex" },
        flexWrap: "wrap",
        gap: 1.5,
        "& > *": { minWidth: { xs: "120px", md: "160px" } },
      }}
    >
      <FormControl size="sm">
        <FormLabel>Status</FormLabel>
        <Select
          value={filters.status || ""}
          onChange={(event, newValue) => {
            const status = newValue === "" ? undefined : newValue;
            onFilterChange({ ...filters, status });
          }}
          size="sm"
          placeholder="Filter by status"
        >
          <Option value="">All</Option>
          <Option value="NEW">New</Option>
          <Option value="IN_PROGRESS">In progress</Option>
          <Option value="COMPLETED">Completed</Option>
        </Select>
      </FormControl>
      <FormControl size="sm">
        <FormLabel>Sort By</FormLabel>
        <Select
          value={filters.sort || ""}
          onChange={(event, newValue) => {
            const sort = newValue || undefined;
            onFilterChange({ ...filters, sort });
          }}
          size="sm"
          placeholder="Sort by"
        >
          <Option value="createdAt">Date</Option>
          <Option value="title">Title</Option>
        </Select>
      </FormControl>
    </Box>
  );
}
