export type TaskResponse = {
  id: number;
  userId: string;
  title: string;
  description: string;
  createdAt: string;
  updatedAt: string;
  status: string;
  dueDate: string;
};

export async function getTasks(): Promise<TaskResponse[]> {
  const token = localStorage.getItem("jwt");
  const res = await fetch("http://localhost:9090/api/v1/tasks", {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  });

  if (!res.ok) {
    throw new Error("Не удалось загрузить задачи");
  }

  return res.json();
}
