import * as React from "react";
import axios from "axios";
import {
  Box,
  Avatar,
  Chip,
  Link,
  Typography,
  List,
  ListItem,
  ListItemContent,
  ListItemDecorator,
  ListDivider,
  IconButton,
  Menu,
  MenuButton,
  MenuItem,
  Dropdown,
  Divider,
} from "@mui/joy";
import { ColorPaletteProp } from "@mui/joy/styles";

import MoreHorizRoundedIcon from "@mui/icons-material/MoreHorizRounded";
import CheckRoundedIcon from "@mui/icons-material/CheckRounded";
import BlockIcon from "@mui/icons-material/Block";
import AutorenewRoundedIcon from "@mui/icons-material/AutorenewRounded";
import KeyboardArrowRightIcon from "@mui/icons-material/KeyboardArrowRight";
import KeyboardArrowLeftIcon from "@mui/icons-material/KeyboardArrowLeft";

interface Task {
  id: number;
  title: string;
  description: string;
  createdAt: string;
  updatedAt: string;
  dueDate: string;
  status: "COMPLETED" | "IN_PROGRESS" | "CANCELLED"; // уточни, если другие
}

function RowMenu() {
  return (
    <Dropdown>
      <MenuButton
        slots={{ root: IconButton }}
        slotProps={{ root: { variant: "plain", color: "neutral", size: "sm" } }}
      >
        <MoreHorizRoundedIcon />
      </MenuButton>
      <Menu size="sm" sx={{ minWidth: 140 }}>
        <MenuItem>Edit</MenuItem>
        <MenuItem>Rename</MenuItem>
        <MenuItem>Move</MenuItem>
        <Divider />
        <MenuItem color="danger">Delete</MenuItem>
      </Menu>
    </Dropdown>
  );
}

export default function OrderList() {
  const [tasks, setTasks] = React.useState<Task[]>([]);
  const [loading, setLoading] = React.useState(true);

  React.useEffect(() => {
    async function fetchTasks() {
      try {
        const token = localStorage.getItem("jwt");
        const res = await axios.get("http://localhost:9090/api/v1/tasks", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setTasks(res.data);
      } catch (error) {
        console.error("Ошибка при загрузке задач:", error);
      } finally {
        setLoading(false);
      }
    }

    fetchTasks();
  }, []);

  if (loading) return <></>;

  return (
    <Box sx={{ display: { xs: "block", sm: "none" } }}>
      {tasks.map((task) => (
        <List key={task.id} size="sm" sx={{ "--ListItem-paddingX": 0 }}>
          <ListItem
            sx={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "start",
            }}
          >
            <ListItemContent sx={{ display: "flex", gap: 2, alignItems: "start" }}>
              <ListItemDecorator>
                <Avatar size="sm">{task.title[0]}</Avatar>
              </ListItemDecorator>
              <div>
                <Typography gutterBottom sx={{ fontWeight: 600 }}>
                  {task.title}
                </Typography>
                <Typography level="body-xs" gutterBottom>
                  {task.description}
                </Typography>
                <Box
                  sx={{
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "space-between",
                    gap: 0.5,
                    mb: 1,
                  }}
                >
                  <Typography level="body-xs">
                    {new Date(task.createdAt).toLocaleDateString()}
                  </Typography>
                  <Typography level="body-xs">&bull;</Typography>
                  <Typography level="body-xs">ID: {task.id}</Typography>
                </Box>
                <Box sx={{ display: "flex", alignItems: "center", gap: 1, mb: 1 }}>
                  <Link level="body-sm" component="button">
                    Подробнее
                  </Link>
                  <RowMenu />
                </Box>
              </div>
            </ListItemContent>
            <Chip
              variant="soft"
              size="sm"
              startDecorator={
                {
                  COMPLETED: <CheckRoundedIcon />,
                  IN_PROGRESS: <AutorenewRoundedIcon />,
                  CANCELLED: <BlockIcon />,
                }[task.status]
              }
              color={
                {
                  COMPLETED: "success",
                  IN_PROGRESS: "neutral",
                  CANCELLED: "danger",
                }[task.status] as ColorPaletteProp
              }
            >
              {task.status}
            </Chip>
          </ListItem>
          <ListDivider />
        </List>
      ))}
      <Box
        className="Pagination-mobile"
        sx={{ display: { xs: "flex", md: "none" }, alignItems: "center", py: 2 }}
      >
        <IconButton aria-label="previous page" variant="outlined" color="neutral" size="sm">
          <KeyboardArrowLeftIcon />
        </IconButton>
        <Typography level="body-sm" sx={{ mx: "auto" }}>
          Page 1 of 1
        </Typography>
        <IconButton aria-label="next page" variant="outlined" color="neutral" size="sm">
          <KeyboardArrowRightIcon />
        </IconButton>
      </Box>
    </Box>
  );
}
