import * as React from "react";
import axios from "axios";
import {
  Avatar,
  Box,
  Button,
  Chip,
  Divider,
  FormControl,
  FormLabel,
  Input,
  Link,
  Modal,
  ModalClose,
  ModalDialog,
  Option,
  Select,
  Sheet,
  Table,
  Typography,
  Checkbox,
  IconButton,
  Menu,
  MenuButton,
  MenuItem,
  Dropdown,
} from "@mui/joy";
import { iconButtonClasses } from "@mui/joy/IconButton";
import {
  FilterAlt as FilterAltIcon,
  Search as SearchIcon,
  ArrowDropDown as ArrowDropDownIcon,
  CheckRounded as CheckRoundedIcon,
  AutorenewRounded as AutorenewRoundedIcon,
  Block as BlockIcon,
  KeyboardArrowLeft as KeyboardArrowLeftIcon,
  KeyboardArrowRight as KeyboardArrowRightIcon,
  MoreHorizRounded as MoreHorizRoundedIcon,
} from "@mui/icons-material";

type Order = "asc" | "desc";

interface Task {
  id: number;
  userId: string;
  title: string;
  description: string;
  createdAt: string;
  updatedAt: string;
  status: "COMPLETED" | "IN_PROGRESS" | "CANCELLED";
  dueDate: string;
}

function descendingComparator<T>(a: T, b: T, orderBy: keyof T) {
  if (b[orderBy] < a[orderBy]) return -1;
  if (b[orderBy] > a[orderBy]) return 1;
  return 0;
}

function getComparator<Key extends keyof any>(
  order: Order,
  orderBy: Key
): (a: { [key in Key]: number | string }, b: { [key in Key]: number | string }) => number {
  return order === "desc"
    ? (a, b) => descendingComparator(a, b, orderBy)
    : (a, b) => -descendingComparator(a, b, orderBy);
}

function RowMenu() {
  return (
    <Dropdown>
      <MenuButton
        slots={{ root: IconButton }}
        slotProps={{ root: { variant: "plain", color: "neutral", size: "sm" } }}
      >
        <MoreHorizRoundedIcon />
      </MenuButton>
      <Menu size="sm" sx={{ minWidth: 140 }}>
        <MenuItem>Edit</MenuItem>
        <MenuItem>Rename</MenuItem>
        <MenuItem>Move</MenuItem>
        <Divider />
        <MenuItem color="danger">Delete</MenuItem>
      </Menu>
    </Dropdown>
  );
}

export default function OrderTable() {
  const [order, setOrder] = React.useState<Order>("desc");
  const [selected, setSelected] = React.useState<readonly number[]>([]);
  const [tasks, setTasks] = React.useState<Task[]>([]);
  const [loading, setLoading] = React.useState(true);
  const [open, setOpen] = React.useState(false);

  React.useEffect(() => {
    async function fetchTasks() {
      try {
        const token = localStorage.getItem("jwt");
        const res = await axios.get("http://localhost:9090/api/v1/tasks", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setTasks(res.data);
      } catch (err) {
        console.error("Ошибка при загрузке задач", err);
      } finally {
        setLoading(false);
      }
    }

    fetchTasks();
  }, []);

  const renderFilters = () => (
    <>
      <FormControl size="sm">
        <FormLabel>Status</FormLabel>
        <Select size="sm" placeholder="Filter by status">
          <Option value="IN_PROGRESS">In progress</Option>
          <Option value="COMPLETED">Completed</Option>
          <Option value="CANCELLED">Cancelled</Option>
        </Select>
      </FormControl>
    </>
  );

  return (
    <React.Fragment>
      {loading ? (
        <Typography level="body-md" sx={{ p: 2 }}>
          Загрузка задач...
        </Typography>
      ) : (
        <>
          <Sheet className="SearchAndFilters-mobile" sx={{ display: { xs: "flex", sm: "none" }, my: 1, gap: 1 }}>
            <Input size="sm" placeholder="Search" startDecorator={<SearchIcon />} sx={{ flexGrow: 1 }} />
            <IconButton size="sm" variant="outlined" color="neutral" onClick={() => setOpen(true)}>
              <FilterAltIcon />
            </IconButton>
            <Modal open={open} onClose={() => setOpen(false)}>
              <ModalDialog aria-labelledby="filter-modal" layout="fullscreen">
                <ModalClose />
                <Typography id="filter-modal" level="h2">Filters</Typography>
                <Divider sx={{ my: 2 }} />
                <Sheet sx={{ display: "flex", flexDirection: "column", gap: 2 }}>
                  {renderFilters()}
                  <Button color="primary" onClick={() => setOpen(false)}>Submit</Button>
                </Sheet>
              </ModalDialog>
            </Modal>
          </Sheet>

          <Sheet
            variant="outlined"
            sx={{
              display: { xs: "none", sm: "block" },
              width: "100%",
              borderRadius: "sm",
              overflow: "auto",
            }}
          >
            <Table stickyHeader hoverRow>
              <thead>
                <tr>
                  <th style={{ width: 48, textAlign: "center", padding: "12px 6px" }}>
                    <Checkbox
                      size="sm"
                      indeterminate={selected.length > 0 && selected.length !== tasks.length}
                      checked={selected.length === tasks.length}
                      onChange={(e) =>
                        setSelected(e.target.checked ? tasks.map((t) => t.id) : [])
                      }
                    />
                  </th>
                  <th style={{ padding: "12px 6px" }}>
                    <Link
                      underline="none"
                      color="primary"
                      component="button"
                      onClick={() => setOrder(order === "asc" ? "desc" : "asc")}
                      endDecorator={<ArrowDropDownIcon />}
                    >
                      Title
                    </Link>
                  </th>
                  <th style={{ padding: "12px 6px" }}>Created At</th>
                  <th style={{ padding: "12px 6px" }}>Status</th>
                  <th style={{ padding: "12px 6px" }}>Actions</th>
                </tr>
              </thead>
              <tbody>
                {[...tasks].sort(getComparator(order, "title")).map((task) => (
                  <tr key={task.id}>
                    <td style={{ textAlign: "center" }}>
                      <Checkbox
                        size="sm"
                        checked={selected.includes(task.id)}
                        onChange={(e) =>
                          setSelected((prev) =>
                            e.target.checked
                              ? [...prev, task.id]
                              : prev.filter((id) => id !== task.id)
                          )
                        }
                      />
                    </td>
                    <td>
                      <Typography level="body-sm">{task.title}</Typography>
                      <Typography level="body-xs" color="neutral">
                        {task.description}
                      </Typography>
                    </td>
                    <td>
                      <Typography level="body-xs">
                        {new Date(task.createdAt).toLocaleDateString()}
                      </Typography>
                    </td>
                    <td>
                      <Chip
                        variant="soft"
                        size="sm"
                        startDecorator={
                          {
                            COMPLETED: <CheckRoundedIcon />,
                            IN_PROGRESS: <AutorenewRoundedIcon />,
                            CANCELLED: <BlockIcon />,
                          }[task.status]
                        }
                        color={
                          {
                            COMPLETED: "success",
                            IN_PROGRESS: "neutral",
                            CANCELLED: "danger",
                          }[task.status] as ColorPaletteProp
                        }
                      >
                        {task.status}
                      </Chip>
                    </td>
                    <td>
                      <Box sx={{ display: "flex", gap: 1, alignItems: "center" }}>
                        <Link level="body-xs" component="button">View</Link>
                        <RowMenu />
                      </Box>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </Sheet>
        </>
      )}
    </React.Fragment>
  );
}
