import * as React from 'react';
import {
  Box,
  Checkbox,
  Typography,
  Avatar,
  Chip,
  Sheet,
  Table,
  IconButton,
  Link,
} from '@mui/joy';
import {
  CheckRounded as CheckRoundedIcon,
  AutorenewRounded as AutorenewRoundedIcon,
  Block as BlockIcon,
  MoreHorizRounded as MoreHorizRoundedIcon,
  KeyboardArrowLeft as KeyboardArrowLeftIcon,
  KeyboardArrowRight as KeyboardArrowRightIcon,
} from '@mui/icons-material';
import { ColorPaletteProp } from '@mui/joy/styles';
import Menu from '@mui/joy/Menu';
import MenuButton from '@mui/joy/MenuButton';
import MenuItem from '@mui/joy/MenuItem';
import Dropdown from '@mui/joy/Dropdown';

function RowMenu() {
  return (
    <Dropdown>
      <MenuButton
        slots={{ root: IconButton }}
        slotProps={{ root: { variant: 'plain', color: 'neutral', size: 'sm' } }}
      >
        <MoreHorizRoundedIcon />
      </MenuButton>
      <Menu size="sm" sx={{ minWidth: 140 }}>
        <MenuItem>Edit</MenuItem>
        <MenuItem>Rename</MenuItem>
        <MenuItem>Move</MenuItem>
        <MenuItem color="danger">Delete</MenuItem>
      </Menu>
    </Dropdown>
  );
}

type TaskRow = {
  id: string;
  date: string;
  status: string;
  customer: {
    initial: string;
    name: string;
    email: string;
  };
};

export default function OrderTable() {
  const [rows, setRows] = React.useState<TaskRow[]>([]);
  const [selected, setSelected] = React.useState<string[]>([]);
  const [order, setOrder] = React.useState<'asc' | 'desc'>('desc');

  React.useEffect(() => {
    async function fetchTasks() {
      try {
        const token = localStorage.getItem('jwt');
        const res = await fetch('http://localhost:9090/api/v1/tasks', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        const data = await res.json();

        const mapped = data.map((task: any) => ({
          id: String(task.id),
          date: new Date(task.createdAt).toLocaleDateString(),
          status: task.status,
          customer: {
            initial: task.userId[0] || '?',
            name: task.userId,
            email: task.userId,
          },
        }));

        setRows(mapped);
      } catch (e) {
        console.error('Ошибка при получении задач:', e);
      }
    }

    fetchTasks();
  }, []);

  return (
    <Sheet
      variant="outlined"
      sx={{
        width: '100%',
        borderRadius: 'sm',
        overflow: 'auto',
        minHeight: 0,
      }}
    >
      <Table
        stickyHeader
        hoverRow
        sx={{
          '--TableCell-headBackground': 'var(--joy-palette-background-level1)',
          '--Table-headerUnderlineThickness': '1px',
          '--TableRow-hoverBackground': 'var(--joy-palette-background-level1)',
          '--TableCell-paddingY': '4px',
          '--TableCell-paddingX': '8px',
        }}
      >
        <thead>
          <tr>
            <th style={{ width: 48, textAlign: 'center', padding: '12px 6px' }}>
              <Checkbox
                size="sm"
                indeterminate={
                  selected.length > 0 && selected.length !== rows.length
                }
                checked={selected.length === rows.length}
                onChange={(e) =>
                  setSelected(e.target.checked ? rows.map((r) => r.id) : [])
                }
              />
            </th>
            <th>ID</th>
            <th>Created At</th>
            <th>Status</th>
            <th>User</th>
            <th />
          </tr>
        </thead>
        <tbody>
          {rows.map((row) => (
            <tr key={row.id}>
              <td style={{ textAlign: 'center' }}>
                <Checkbox
                  size="sm"
                  checked={selected.includes(row.id)}
                  onChange={(e) => {
                    setSelected((prev) =>
                      e.target.checked
                        ? [...prev, row.id]
                        : prev.filter((id) => id !== row.id)
                    );
                  }}
                />
              </td>
              <td>
                <Typography level="body-xs">{row.id}</Typography>
              </td>
              <td>
                <Typography level="body-xs">{row.date}</Typography>
              </td>
              <td>
                <Chip
                  variant="soft"
                  size="sm"
                  startDecorator={
                    {
                      COMPLETED: <CheckRoundedIcon />,
                      IN_PROGRESS: <AutorenewRoundedIcon />,
                      CANCELLED: <BlockIcon />,
                    }[row.status]
                  }
                  color={
                    {
                      COMPLETED: 'success',
                      IN_PROGRESS: 'neutral',
                      CANCELLED: 'danger',
                    }[row.status] as ColorPaletteProp
                  }
                >
                  {row.status}
                </Chip>
              </td>
              <td>
                <Box sx={{ display: 'flex', gap: 2, alignItems: 'center' }}>
                  <Avatar size="sm">{row.customer.initial}</Avatar>
                  <Box>
                    <Typography level="body-xs">{row.customer.name}</Typography>
                    <Typography level="body-xs">{row.customer.email}</Typography>
                  </Box>
                </Box>
              </td>
              <td>
                <Box sx={{ display: 'flex', gap: 2 }}>
                  <Link level="body-xs" component="button">
                    Download
                  </Link>
                  <RowMenu />
                </Box>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Sheet>
  );
}
