import * as React from "react";
import {
  Box,
  CircularProgress,
  Typography,
  List,
  ListItem,
  ListItemContent,
  ListItemDecorator,
  ListDivider,
  Avatar,
  Link,
  Chip,
  IconButton,
} from "@mui/joy";
import MoreHorizRoundedIcon from "@mui/icons-material/MoreHorizRounded";
import CheckRoundedIcon from "@mui/icons-material/CheckRounded";
import BlockIcon from "@mui/icons-material/Block";
import AutorenewRoundedIcon from "@mui/icons-material/AutorenewRounded";
import KeyboardArrowLeftIcon from "@mui/icons-material/KeyboardArrowLeft";
import KeyboardArrowRightIcon from "@mui/icons-material/KeyboardArrowRight";
import { getTasks, TaskResponse } from "../../api/task";

function RowMenu() {
  return (
    <IconButton variant="plain" color="neutral" size="sm">
      <MoreHorizRoundedIcon />
    </IconButton>
  );
}

export default function TaskList() {
  const [tasks, setTasks] = React.useState<TaskResponse[]>([]);
  const [loading, setLoading] = React.useState(true);

  React.useEffect(() => {
    async function fetchData() {
      try {
        const data = await getTasks();
        setTasks(data);
      } catch (err) {
        console.error("Ошибка при загрузке задач:", err);
      } finally {
        setLoading(false);
      }
    }

    fetchData();
  }, []);

  if (loading) {
    return (
      <Box sx={{ display: "flex", justifyContent: "center", mt: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box sx={{ display: { xs: "block", sm: "none" }, px: 2 }}>
      {tasks.map((task) => (
        <List key={task.id} size="sm" sx={{ "--ListItem-paddingX": 0 }}>
          <ListItem
            sx={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "start",
            }}
          >
            <ListItemContent sx={{ display: "flex", gap: 2 }}>
              <ListItemDecorator>
                <Avatar size="sm">{task.title[0]}</Avatar>
              </ListItemDecorator>
              <Box>
                <Typography fontWeight={600}>{task.title}</Typography>
                <Typography level="body-xs" noWrap sx={{ maxWidth: 240 }}>
                  {task.description}
                </Typography>
                <Box
                  sx={{
                    display: "flex",
                    gap: 1,
                    flexWrap: "wrap",
                    alignItems: "center",
                    mb: 1,
                  }}
                >
                  <Typography level="body-xs">
                    {new Date(task.createdAt).toLocaleDateString()}
                  </Typography>
                  <Typography level="body-xs">&bull;</Typography>
                  <Typography level="body-xs">ID: {task.id}</Typography>
                </Box>
                <Box sx={{ display: "flex", gap: 1, alignItems: "center" }}>
                  <Link level="body-sm" component="button">
                    Подробнее
                  </Link>
                  <RowMenu />
                </Box>
              </Box>
            </ListItemContent>
            <Chip
              variant="soft"
              size="sm"
              startDecorator={
                {
                  COMPLETED: <CheckRoundedIcon />,
                  IN_PROGRESS: <AutorenewRoundedIcon />,
                  CANCELLED: <BlockIcon />,
                }[task.status]
              }
              color={
                {
                  COMPLETED: "success",
                  IN_PROGRESS: "neutral",
                  CANCELLED: "danger",
                }[task.status] ?? "neutral"
              }
            >
              {task.status}
            </Chip>
          </ListItem>
          <ListDivider />
        </List>
      ))}
      <Box
        className="Pagination-mobile"
        sx={{ display: "flex", alignItems: "center", py: 2 }}
      >
        <IconButton aria-label="previous page" variant="outlined" color="neutral" size="sm">
          <KeyboardArrowLeftIcon />
        </IconButton>
        <Typography level="body-sm" sx={{ mx: "auto" }}>
          Page 1 of 1
        </Typography>
        <IconButton aria-label="next page" variant="outlined" color="neutral" size="sm">
          <KeyboardArrowRightIcon />
        </IconButton>
      </Box>
    </Box>
  );
}
