import * as React from "react";
import {
  Box,
  Avatar,
  Chip,
  Typography,
  List,
  ListItem,
  ListItemContent,
  ListItemDecorator,
  ListDivider,
  IconButton,
  Menu,
  MenuButton,
  MenuItem,
  Dropdown,
  Divider,
  CircularProgress,
} from "@mui/joy";
import { ColorPaletteProp } from "@mui/joy/styles";

import MoreHorizRoundedIcon from "@mui/icons-material/MoreHorizRounded";
import CheckRoundedIcon from "@mui/icons-material/CheckRounded";
import BlockIcon from "@mui/icons-material/Block";
import AutorenewRoundedIcon from "@mui/icons-material/AutorenewRounded";
import KeyboardArrowRightIcon from "@mui/icons-material/KeyboardArrowRight";
import KeyboardArrowLeftIcon from "@mui/icons-material/KeyboardArrowLeft";

import { getTasks, TaskResponse } from "../../api/task";
import TaskFilters, { Filters } from "./TaskFilters";

function RowMenu({
  onEdit,
  onDelete,
}: {
  onEdit?: () => void;
  onDelete?: () => void;
}) {
  return (
    <Dropdown>
      <MenuButton
        slots={{ root: IconButton }}
        slotProps={{ root: { variant: "plain", color: "neutral", size: "sm" } }}
      >
        <MoreHorizRoundedIcon />
      </MenuButton>
      <Menu size="sm" sx={{ minWidth: 140 }}>
        <MenuItem onClick={onEdit}>Edit</MenuItem>
        <Divider />
        <MenuItem color="danger" onClick={onDelete}>
          Delete
        </MenuItem>
      </Menu>
    </Dropdown>
  );
}

export default function TaskList() {
  const [tasks, setTasks] = React.useState<TaskResponse[]>([]);
  const [loading, setLoading] = React.useState(true);

  // Добавляем состояние для фильтров
  const [filters, setFilters] = React.useState<Filters>({});

  React.useEffect(() => {
    async function fetchData() {
      setLoading(true);
      try {
        // Передаём фильтры в getTasks, чтобы получать отфильтрованные данные
        const data = await getTasks(filters);
        setTasks(data);
      } catch (err) {
        console.error("Ошибка при загрузке задач:", err);
      } finally {
        setLoading(false);
      }
    }

    fetchData();
  }, [JSON.stringify(filters)]); // пересоздаём при изменении фильтров

  if (loading) {
    return (
      <Box sx={{ display: "flex", justifyContent: "center", mt: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <>
      {/* Передаём setFilters, чтобы TaskFilters мог обновлять фильтры */}
      <TaskFilters onFilterChange={setFilters} />
      <Box sx={{ display: { xs: "block", sm: "none" }, px: 2 }}>
        {tasks.map((task) => (
          <List key={task.id} size="sm" sx={{ "--ListItem-paddingX": 0 }}>
            <ListItem
              sx={{
                display: "flex",
                justifyContent: "space-between",
                alignItems: "start",
                mb: 2,
              }}
            >
              <ListItemContent
                sx={{ display: "flex", gap: 2, alignItems: "start" }}
              >
                <ListItemDecorator>
                  <Avatar size="sm">{task.title[0]}</Avatar>
                </ListItemDecorator>
                <div>
                  <Typography gutterBottom sx={{ fontWeight: 600 }}>
                    {task.title}
                  </Typography>
                  <Typography
                    level="body-xs"
                    gutterBottom
                    noWrap
                    sx={{ maxWidth: 240 }}
                  >
                    {task.description}
                  </Typography>
                  <Box
                    sx={{
                      display: "flex",
                      alignItems: "center",
                      gap: 0.5,
                      mb: 1,
                      flexWrap: "wrap",
                    }}
                  >
                    <Typography level="body-xs">
                      Created: {new Date(task.createdAt).toLocaleDateString()}
                    </Typography>
                    <Typography level="body-xs">&bull;</Typography>
                    <Typography level="body-xs">ID: {task.id}</Typography>
                  </Box>
                  <Typography level="body-xs">Until: {task.dueDate}</Typography>
                </div>
              </ListItemContent>
              <Box
                sx={{
                  display: "flex",
                  flexDirection: "column",
                  alignItems: "flex-end",
                  gap: 1,
                }}
              >
                <Chip
                  variant="soft"
                  size="sm"
                  startDecorator={
                    {
                      Completed: <CheckRoundedIcon />,
                      In_progress: <AutorenewRoundedIcon />,
                      New: <BlockIcon />,
                    }[task.status]
                  }
                  color={
                    {
                      Completed: "success",
                      In_progress: "neutral",
                      New: "primary",
                    }[task.status] as ColorPaletteProp
                  }
                >
                  {task.status}
                </Chip>
                <RowMenu />
              </Box>
            </ListItem>
            <ListDivider />
          </List>
        ))}
        <Box
          className="Pagination-mobile"
          sx={{
            display: { xs: "flex", md: "none" },
            alignItems: "center",
            py: 2,
          }}
        >
          <IconButton
            aria-label="previous page"
            variant="outlined"
            color="neutral"
            size="sm"
          >
            <KeyboardArrowLeftIcon />
          </IconButton>
          <Typography level="body-sm" sx={{ mx: "auto" }}>
            Page 1 of 1
          </Typography>
          <IconButton
            aria-label="next page"
            variant="outlined"
            color="neutral"
            size="sm"
          >
            <KeyboardArrowRightIcon />
          </IconButton>
        </Box>
      </Box>
    </>
  );
}
