import {
  Box,
  Table,
  Checkbox,
  Chip,
  Typography,
  Sheet,
  CircularProgress,
} from "@mui/joy";
import { ColorPaletteProp } from "@mui/joy/styles";
import CheckRoundedIcon from "@mui/icons-material/CheckRounded";
import AutorenewRoundedIcon from "@mui/icons-material/AutorenewRounded";
import BlockIcon from "@mui/icons-material/Block";
import { useState } from "react";
import RowMenu from "./RowMenu";

export type RowType = {
  dueDate: string;
  description: string;
  id: string;
  title: string;
  date: string;
  status: string;
  until: string;
}

interface TaskTableProps {
  rows: RowType[];
  loading: boolean;
  onDelete: (id: string) => void;
  onEdit: (id: string) => void;
}

export default function TaskTable({
  rows,
  loading,
  onDelete,
  onEdit,
}: TaskTableProps) {
  const [selected, setSelected] = useState<readonly string[]>([]);
  const [order, setOrder] = useState<"asc" | "desc">("desc");

  if (loading) {
    return (
      <Box
        sx={{
          height: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          flexDirection: "column",
          gap: 2,
        }}
      >
        <CircularProgress />
        <Typography level="title-sm">Загрузка...</Typography>
      </Box>
    );
  }

  return (
    <Sheet
      variant="outlined"
      sx={{
        display: { xs: "none", sm: "initial" },
        width: "100%",
        borderRadius: "sm",
        flexShrink: 1,
        overflow: "auto",
        minHeight: 0,
      }}
    >
      <Table
        stickyHeader
        hoverRow
        sx={{
          "--TableCell-headBackground": "var(--joy-palette-background-level1)",
          "--Table-headerUnderlineThickness": "1px",
          "--TableRow-hoverBackground": "var(--joy-palette-background-level1)",
          "--TableCell-paddingY": "4px",
          "--TableCell-paddingX": "8px",
        }}
      >
        <thead>
          <tr>
            <th>Task</th>
            <th>Title</th>
            <th>Date</th>
            <th>Status</th>
            <th>Until</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {rows.map((row) => (
            <tr key={row.id}>
              <td className="padding-left: 20px">{row.id}</td>
              <td>{row.title}</td>
              <td>{row.date}</td>
              <td>
                <Chip
                  variant="soft"
                  size="sm"
                  startDecorator={
                    {
                      Completed: <CheckRoundedIcon />,
                      Refunded: <AutorenewRoundedIcon />,
                      Cancelled: <BlockIcon />,
                    }[row.status]
                  }
                  color={
                    {
                      Completed: "success",
                      Refunded: "neutral",
                      Cancelled: "danger",
                    }[row.status] as ColorPaletteProp
                  }
                >
                  {row.status}
                </Chip>
              </td>
              <td>{row.until != null ? row.until.substring(0, 10) : null}</td>
              <td>
                <RowMenu
                  onDelete={() => onDelete(row.id)}
                  onEdit={() => onEdit(row.id)}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      {/* Пагинация можно тоже вынести отдельно */}
    </Sheet>
  );
}
