import { Box } from "@mui/joy";
import TaskList from "../../components/dashboard/TaskList";
import DashboardHeader from "./DashboardHeader";
import TaskTable, { RowType } from "../../components/dashboard/TaskTable";
import Sidebar from "../../components/dashboard/Sidebar";
import { useTasks } from "../../hooks/useTasks";
import { useState } from "react";
import EditTaskModal from "../../components/dashboard/EditTaskModal";

export default function DashboardContent() {
  const { rows, loading, deleteTask, refresh, updateTask } = useTasks();

  const [editingTask, setEditingTask] = useState<null | (typeof rows)[0]>(null);

  const handleDelete = async (id: string) => {
    await deleteTask(id);
    refresh();
  };

  const handleEdit = (id: string) => {
    const task = rows.find((r) => r.id === id);
    if (task) setEditingTask(task);
  };

  const handleUpdate = async (updatedTask: RowType) => {
    await updateTask({
      id: updatedTask.id,
      title: updatedTask.title,
      description: updatedTask.description || "", // если возможно, добавить описание
      dueDate: updatedTask.dueDate || "",
      status: updatedTask.status,
    });
    setEditingTask(null);
    refresh();
  };

  const handleCloseEdit = () => {
    setEditingTask(null);
  };

  return (
    <Box sx={{ display: "flex", minHeight: "100dvh" }}>
      <Sidebar onTaskCreated={refresh} />

      <Box
        component="main"
        className="MainContent"
        sx={{
          px: { xs: 2, md: 6 },
          pt: {
            xs: "calc(12px + var(--Header-height))",
            sm: "calc(12px + var(--Header-height))",
            md: 3,
          },
          pb: { xs: 2, sm: 2, md: 3 },
          flex: 1,
          display: "flex",
          flexDirection: "column",
          minWidth: 0,
          height: "100dvh",
          gap: 1,
        }}
      >
        <DashboardHeader />
        <TaskTable
          rows={rows}
          loading={loading}
          onDelete={handleDelete}
          onEdit={handleEdit}
        />
        <TaskList />

        {/* Модалка редактирования */}
        {editingTask && (
          <EditTaskModal
            task={{
              id: editingTask.id,
              title: editingTask.title,
              description: editingTask.description,
              dueDate: editingTask.dueDate,
              status: editingTask.status
            }}
            onClose={handleCloseEdit}
            onSave={handleUpdate}
          />
        )}
      </Box>
    </Box>
  );
}
