import * as React from "react";
import { ColorPaletteProp } from "@mui/joy/styles";
import {
  Avatar,
  Box,
  Button,
  Chip,
  Divider,
  FormControl,
  FormLabel,
  Input,
  Modal,
  ModalDialog,
  ModalClose,
  Option,
  Select,
  Sheet,
  Table,
  Checkbox,
  IconButton,
  Typography,
  Menu,
  MenuButton,
  MenuItem,
  Dropdown,
} from "@mui/joy";

import {
  FilterAlt as FilterAltIcon,
  Search as SearchIcon,
  ArrowDropDown as ArrowDropDownIcon,
  CheckRounded as CheckRoundedIcon,
  AutorenewRounded as AutorenewRoundedIcon,
  Block as BlockIcon,
  KeyboardArrowRight as KeyboardArrowRightIcon,
  KeyboardArrowLeft as KeyboardArrowLeftIcon,
  MoreHorizRounded as MoreHorizRoundedIcon,
} from "@mui/icons-material";

import { iconButtonClasses } from "@mui/joy/IconButton";
import { useTasks } from "../hooks/useTasks";

function descendingComparator<T>(a: T, b: T, orderBy: keyof T) {
  if (b[orderBy] < a[orderBy]) return -1;
  if (b[orderBy] > a[orderBy]) return 1;
  return 0;
}

function getComparator<Key extends keyof any>(
  order: "asc" | "desc",
  orderBy: Key
): (
  a: { [key in Key]: number | string },
  b: { [key in Key]: number | string }
) => number {
  return order === "desc"
    ? (a, b) => descendingComparator(a, b, orderBy)
    : (a, b) => -descendingComparator(a, b, orderBy);
}

function RowMenu({
  onDelete,
  onEdit,
}: {
  onDelete: () => void;
  onEdit: () => void;
}) {
  return (
    <Dropdown>
      <MenuButton
        slots={{ root: IconButton }}
        slotProps={{ root: { variant: "plain", color: "neutral", size: "sm" } }}
      >
        <MoreHorizRoundedIcon />
      </MenuButton>
      <Menu size="sm" sx={{ minWidth: 140 }}>
        <MenuItem onClick={onEdit}>Edit</MenuItem>
        <Divider />
        <MenuItem color="danger" onClick={onDelete}>
          Delete
        </MenuItem>
      </Menu>
    </Dropdown>
  );
}

export default function OrderTable() {
  const { rows, loading, refresh, deleteTask, editTask } = useTasks();

  const [order, setOrder] = React.useState<"asc" | "desc">("desc");
  const [selected, setSelected] = React.useState<readonly string[]>([]);
  const [filterOpen, setFilterOpen] = React.useState(false);

  // Для редактирования
  const [editModalOpen, setEditModalOpen] = React.useState(false);
  const [editTaskData, setEditTaskData] = React.useState<{
    id: string;
    title: string;
    status: string;
  } | null>(null);

  const handleEditOpen = (id: string) => {
    const task = rows.find((r) => r.id === id);
    if (task) {
      setEditTaskData({ id: task.id, title: task.title, status: task.status });
      setEditModalOpen(true);
    }
  };

  const handleEditClose = () => {
    setEditModalOpen(false);
    setEditTaskData(null);
  };

  const handleEditSave = async () => {
    if (!editTaskData) return;
    try {
      await editTask(editTaskData.id, {
        title: editTaskData.title,
        status: editTaskData.status,
      });
      await refresh();
      handleEditClose();
    } catch (err) {
      console.error("Ошибка при обновлении задачи", err);
      alert("Не удалось обновить задачу");
    }
  };

  const handleDelete = async (id: string) => {
    if (window.confirm("Удалить задачу?")) {
      await deleteTask(id);
      await refresh();
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: "flex", justifyContent: "center", py: 4 }}>
        <Typography level="h3">Загрузка задач...</Typography>
      </Box>
    );
  }

  const renderFilters = () => (
    <>
      <FormControl size="sm">
        <FormLabel>Status</FormLabel>
        <Select size="sm" placeholder="Filter by status">
          <Option value="NEW">New</Option>
          <Option value="IN_PROGRESS">In progress</Option>
          <Option value="COMPLETED">Completed</Option>
        </Select>
      </FormControl>
      <FormControl size="sm">
        <FormLabel>Sort By</FormLabel>
        <Select size="sm" placeholder="Date">
          <Option value="createdAt">Date</Option>
          <Option value="title">Title</Option>
        </Select>
      </FormControl>
    </>
  );

  return (
    <>
      {/* Модалка фильтров */}
      <Sheet
        className="SearchAndFilters-mobile"
        sx={{ display: { xs: "flex", sm: "none" }, my: 1, gap: 1 }}
      >
        <IconButton
          size="sm"
          variant="outlined"
          color="neutral"
          onClick={() => setFilterOpen(true)}
        >
          <FilterAltIcon />
        </IconButton>
        <Modal open={filterOpen} onClose={() => setFilterOpen(false)}>
          <ModalDialog aria-labelledby="filter-modal" layout="fullscreen">
            <ModalClose />
            <Typography id="filter-modal" level="h2">
              Filters
            </Typography>
            <Divider sx={{ my: 2 }} />
            <Sheet sx={{ display: "flex", flexDirection: "column", gap: 2 }}>
              {renderFilters()}
              <Button color="primary" onClick={() => setFilterOpen(false)}>
                Submit
              </Button>
            </Sheet>
          </ModalDialog>
        </Modal>
      </Sheet>

      {/* Панель поиска и фильтров для планшетов и больше */}
      <Box
        className="SearchAndFilters-tabletUp"
        sx={{
          borderRadius: "sm",
          py: 2,
          display: { xs: "none", sm: "flex" },
          flexWrap: "wrap",
          gap: 1.5,
          "& > *": { minWidth: { xs: "120px", md: "160px" } },
        }}
      >
        <FormControl sx={{ flex: 1 }} size="sm">
          <FormLabel>Search for task</FormLabel>
          <Input size="sm" placeholder="Search" startDecorator={<SearchIcon />} />
        </FormControl>
        {renderFilters()}
      </Box>

      {/* Таблица задач */}
      <Sheet
        className="TaskTableContainer"
        variant="outlined"
        sx={{
          display: { xs: "none", sm: "initial" },
          width: "100%",
          borderRadius: "sm",
          flexShrink: 1,
          overflow: "auto",
          minHeight: 0,
        }}
      >
        <Table
          stickyHeader
          hoverRow
          sx={{
            "--TableCell-headBackground": "var(--joy-palette-background-level1)",
            "--Table-headerUnderlineThickness": "1px",
            "--TableRow-hoverBackground": "var(--joy-palette-background-level1)",
            "--TableCell-paddingY": "4px",
            "--TableCell-paddingX": "8px",
          }}
        >
          <thead>
            <tr>
              <th style={{ width: 48 }}>
                <Checkbox
                  size="sm"
                  indeterminate={
                    selected.length > 0 && selected.length !== rows.length
                  }
                  checked={selected.length === rows.length}
                  onChange={(event) => {
                    setSelected(event.target.checked ? rows.map((r) => r.id) : []);
                  }}
                />
              </th>
              <th>Task</th>
              <th>Title</th>
              <th>Date</th>
              <th>Status</th>
              <th>Customer</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {[...rows].sort(getComparator(order, "id")).map((row) => (
              <tr key={row.id}>
                <td>
                  <Checkbox
                    size="sm"
                    checked={selected.includes(row.id)}
                    onChange={(event) => {
                      setSelected((ids) =>
                        event.target.checked
                          ? [...ids, row.id]
                          : ids.filter((id) => id !== row.id)
                      );
                    }}
                  />
                </td>
                <td>{row.id}</td>
                <td>{row.title}</td>
                <td>{row.date}</td>
                <td>
                  <Chip
                    variant="soft"
                    size="sm"
                    startDecorator={
                      {
                        Completed: <CheckRoundedIcon />,
                        Refunded: <AutorenewRoundedIcon />,
                        Cancelled: <BlockIcon />,
                      }[row.status]
                    }
                    color={
                      {
                        Completed: "success",
                        Refunded: "neutral",
                        Cancelled: "danger",
                      }[row.status] as ColorPaletteProp
                    }
                  >
                    {row.status}
                  </Chip>
                </td>
                <td>
                  <Box sx={{ display: "flex", alignItems: "center", gap: 2 }}>
                    <Avatar size="sm">{row.customer.initial}</Avatar>
                    <Box>
                      <Typography level="body-sm">{row.customer.name}</Typography>
                      <Typography level="body-xs">{row.customer.email}</Typography>
                    </Box>
                  </Box>
                </td>
                <td>
                  <RowMenu
                    onDelete={() => handleDelete(row.id)}
                    onEdit={() => handleEditOpen(row.id)}
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </Sheet>

      {/* Модалка редактирования */}
      <Modal open={editModalOpen} onClose={handleEditClose}>
        <ModalDialog aria-labelledby="edit-task-modal">
          <ModalClose />
          <Typography id="edit-task-modal" level="h2" mb={2}>
            Редактировать задачу
          </Typography>

          <FormControl sx={{ mb: 2 }}>
            <FormLabel>Title</FormLabel>
            <Input
              value={editTaskData?.title ?? ""}
              onChange={(e) =>
                setEditTaskData((prev) =>
                  prev ? { ...prev, title: e.target.value } : prev
                )
              }
            />
          </FormControl>

          <FormControl sx={{ mb: 2 }}>
            <FormLabel>Status</FormLabel>
            <Select
              value={editTaskData?.status ?? ""}
              onChange={(e, value) =>
                setEditTaskData((prev) =>
                  prev ? { ...prev, status: value ?? prev.status } : prev
                )
              }
            >
              <Option value="New">New</Option>
              <Option value="In_progress">In progress</Option>
              <Option value="Completed">Completed</Option>
            </Select>
          </FormControl>

          <Box sx={{ display: "flex", gap: 1, justifyContent: "flex-end" }}>
            <Button variant="outlined" color="neutral" onClick={handleEditClose}>
              Cancel
            </Button>
            <Button variant="solid" color="primary" onClick={handleEditSave}>
              Save
            </Button>
          </Box>
        </ModalDialog>
      </Modal>

      {/* Пагинация */}
      <Box
        className="Pagination-laptopUp"
        sx={{
          pt: 2,
          gap: 1,
          [`& .${iconButtonClasses.root}`]: { borderRadius: "50%" },
          display: { xs: "none", md: "flex" },
        }}
      >
        <Button
          size="sm"
          variant="outlined"
          color="neutral"
          startDecorator={<KeyboardArrowLeftIcon />}
        >
          Previous
        </Button>
        <Box sx={{ flex: 1 }} />
        {["1", "2", "3", "...", "8", "9", "10"].map((page) => (
          <IconButton
            key={page}
            size="sm"
            variant={Number(page) ? "outlined" : "plain"}
          >
            {page}
          </IconButton>
        ))}
        <Box sx={{ flex: 1 }} />
        <Button
          size="sm"
          variant="outlined"
          color="neutral"
          endDecorator={<KeyboardArrowRightIcon />}
        >
          Next
        </Button>
      </Box>
    </>
  );

}
