import { Box, FormControl, FormLabel, Select, Option } from "@mui/joy";
import { useEffect, useState } from "react";

export interface Filters {
  status?: "NEW" | "IN_PROGRESS" | "COMPLETED";
  sort?: "createdAt" | "title";
}

interface TaskFiltersProps {
  filters?: Filters; // контролируемые фильтры
  onFilterChange?: (filters: Filters) => void;
}

export default function TaskFilters({
  filters: controlledFilters,
  onFilterChange,
}: TaskFiltersProps) {
  const [status, setStatus] = useState<Filters["status"]>(
    controlledFilters?.status
  );
  const [sortBy, setSortBy] = useState<Filters["sort"]>(
    controlledFilters?.sort
  );

  // Синхронизация локального состояния при изменении контролируемых фильтров
  useEffect(() => {
    if (controlledFilters) {
      setStatus(controlledFilters.status);
      setSortBy(controlledFilters.sort);
    }
  }, [controlledFilters]);

  // Вызываем onFilterChange при изменении фильтров
  useEffect(() => {
    onFilterChange?.({
      status,
      sort: sortBy,
    });
  }, [status, sortBy, onFilterChange]);

  return (
    <Box
      sx={{
        borderRadius: "sm",
        py: 2,
        display: { xs: "none", sm: "flex" },
        flexWrap: "wrap",
        gap: 1.5,
        "& > *": { minWidth: { xs: "120px", md: "160px" } },
      }}
    >
      <FormControl size="sm">
        <FormLabel>Status</FormLabel>
        <Select
          value={status}
          onChange={(_, v) => setStatus((v as Filters["status"]) || undefined)}
          size="sm"
          placeholder="Filter by status"
        >
          <Option value="NEW">New</Option>
          <Option value="IN_PROGRESS">In progress</Option>
          <Option value="COMPLETED">Completed</Option>
        </Select>
      </FormControl>
      <FormControl size="sm">
        <FormLabel>Sort By</FormLabel>
        <Select
          value={sortBy}
          onChange={(_, v) => setSortBy((v as Filters["sort"]) || undefined)}
          size="sm"
          placeholder="Sort by"
        >
          <Option value="createdAt">Date</Option>
          <Option value="title">Title</Option>
        </Select>
      </FormControl>
    </Box>
  );
}
