import {
  Box,
  FormControl,
  FormLabel,
  Select,
  Option,
  Input,
  IconButton,
} from "@mui/joy";
import SearchIcon from "@mui/icons-material/Search";
import FilterAltIcon from "@mui/icons-material/FilterAlt";
import { useEffect, useState } from "react";

interface TaskFiltersProps {
  onFilterChange?: (filters: any) => void; // подгони типизацию под свой кейс
}

export default function TaskFilters({ onFilterChange }: TaskFiltersProps) {
  const [status, setStatus] = useState<string | undefined>();
  const [sortBy, setSortBy] = useState<string | undefined>();
  const [search, setSearch] = useState("");

  useEffect(() => {
    const filters: Record<string, string> = {};
    if (status) filters.status = status;
    if (sortBy) filters.sort = sortBy;
    if (search) filters.search = search;

    onFilterChange?.(filters);
  }, [status, sortBy, search]);

  return (
    <>
      <Box
        sx={{
          borderRadius: "sm",
          py: 2,
          display: { xs: "none", sm: "flex" },
          flexWrap: "wrap",
          gap: 1.5,
          "& > *": { minWidth: { xs: "120px", md: "160px" } },
        }}
      >
        <FormControl sx={{ flex: 1 }} size="sm">
          <FormLabel>Search for task</FormLabel>
          <Input
            size="sm"
            placeholder="Search"
            startDecorator={<SearchIcon />}
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
        </FormControl>
        <FormControl size="sm">
          <FormLabel>Status</FormLabel>
          <Select
            value={status}
            onChange={(_, v) => setStatus(v || undefined)}
            size="sm"
            placeholder="Filter by status"
          >
            <Option value="NEW">New</Option>
            <Option value="IN_PROGRESS">In progress</Option>
            <Option value="COMPLETED">Completed</Option>
          </Select>
        </FormControl>
        <FormControl size="sm">
          <FormLabel>Sort By</FormLabel>
          <Select
            value={sortBy}
            onChange={(_, v) => setSortBy(v || undefined)}
            size="sm"
            placeholder="Sort by"
          >
            <Option value="createdAt">Date</Option>
            <Option value="title">Title</Option>
          </Select>
        </FormControl>
      </Box>
    </>
  );
}
