import * as React from "react";
import {
  Box,
  Avatar,
  Chip,
  Typography,
  List,
  ListItem,
  ListItemContent,
  ListItemDecorator,
  ListDivider,
  IconButton,
  Menu,
  MenuButton,
  MenuItem,
  Dropdown,
  Divider,
  CircularProgress,
  Button,
} from "@mui/joy";
import { ColorPaletteProp } from "@mui/joy/styles";

import MoreHorizRoundedIcon from "@mui/icons-material/MoreHorizRounded";
import CheckRoundedIcon from "@mui/icons-material/CheckRounded";
import BlockIcon from "@mui/icons-material/Block";
import AutorenewRoundedIcon from "@mui/icons-material/AutorenewRounded";
import KeyboardArrowRightIcon from "@mui/icons-material/KeyboardArrowRight";
import KeyboardArrowLeftIcon from "@mui/icons-material/KeyboardArrowLeft";

import TaskFilters, { Filters } from "./TaskFilters";

function RowMenu({
  onEdit,
  onDelete,
}: {
  onEdit?: () => void;
  onDelete?: () => void;
}) {
  return (
    <Dropdown>
      <MenuButton
        slots={{ root: IconButton }}
        slotProps={{ root: { variant: "plain", color: "neutral", size: "sm" } }}
      >
        <MoreHorizRoundedIcon />
      </MenuButton>
      <Menu size="sm" sx={{ minWidth: 140 }}>
        <MenuItem onClick={onEdit}>Edit</MenuItem>
        <Divider />
        <MenuItem color="danger" onClick={onDelete}>
          Delete
        </MenuItem>
      </Menu>
    </Dropdown>
  );
}

export interface RowType {
  dueDate: string;
  description: string;
  id: string;
  title: string;
  date: string;
  status: string;
  until: string;
}

interface TaskListProps {
  rows: RowType[];
  loading: boolean;
  onDelete: (id: string) => void;
  onEdit: (id: string) => void;
}

export default function TaskList({
  rows,
  loading,
  onDelete,
  onEdit,
}: TaskListProps) {
  const [filters, setFilters] = React.useState<Filters>({ sort: "createdAt" });

  // Debug: Log rows and filters
  console.log("TaskList rows:", rows);
  console.log("TaskList filters:", filters);

  // Apply client-side filtering
  const filteredRows = React.useMemo(() => {
    let result = [...rows];
    if (filters.status) {
      result = result.filter((row) => row.status === filters.status);
    }
    if (filters.sort) {
      result.sort((a, b) => {
        if (filters.sort === "createdAt") {
          return new Date(a.date).getTime() - new Date(b.date).getTime();
        }
        if (filters.sort === "title") {
          return a.title.localeCompare(b.title);
        }
        return 0;
      });
    }
    return result;
  }, [rows, filters]);

  return (
    <Box sx={{ display: "flex", flexDirection: "column", minHeight: 0 }}>
      {/* Filters always above */}
      <Box sx={{ mb: 2, order: 1 }}>
        <Box sx={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
          <TaskFilters filters={filters} onFilterChange={setFilters} />
          <Button onClick={() => setFilters({})}>Reset Filters</Button>
        </Box>
      </Box>

      {/* List for all screen sizes */}
      <Box sx={{ order: 2, px: 2 }}>
        {loading ? (
          <Box sx={{ display: "flex", justifyContent: "center", alignItems: "center", py: 4 }}>
            <CircularProgress />
            <Typography level="title-sm" sx={{ ml: 2 }}>
              Loading...
            </Typography>
          </Box>
        ) : filteredRows.length === 0 ? (
          <Typography level="body-md" sx={{ textAlign: "center", py: 4 }}>
            No tasks found.
          </Typography>
        ) : (
          <>
            {filteredRows.map((row) => (
              <List key={row.id} size="sm" sx={{ "--ListItem-paddingX": 0 }}>
                <ListItem
                  sx={{
                    display: "flex",
                    justifyContent: "space-between",
                    alignItems: "start",
                    mb: 2,
                  }}
                >
                  <ListItemContent
                    sx={{ display: "flex", gap: 2, alignItems: "start" }}
                  >
                    <ListItemDecorator>
                      <Avatar size="sm">{row.title[0]}</Avatar>
                    </ListItemDecorator>
                    <div>
                      <Typography gutterBottom sx={{ fontWeight: 600 }}>
                        {row.title}
                      </Typography>
                      <Typography
                        level="body-xs"
                        gutterBottom
                        noWrap
                        sx={{ maxWidth: 240 }}
                      >
                        {row.description}
                      </Typography>
                      <Box
                        sx={{
                          display: "flex",
                          alignItems: "center",
                          gap: 0.5,
                          mb: 1,
                          flexWrap: "wrap",
                        }}
                      >
                        <Typography level="body-xs">
                          Created: {new Date(row.date).toLocaleDateString()}
                        </Typography>
                        <Typography level="body-xs">•</Typography>
                        <Typography level="body-xs">ID: {row.id}</Typography>
                      </Box>
                      <Typography level="body-xs">
                        Until: {row.until}
                      </Typography>
                    </div>
                  </ListItemContent>
                  <Box
                    sx={{
                      display: "flex",
                      flexDirection: "column",
                      alignItems: "flex-end",
                      gap: 1,
                    }}
                  >
                    <Chip
                      variant="soft"
                      size="sm"
                      startDecorator={
                        {
                          New: <CheckRoundedIcon />,
                          In_progress: <AutorenewRoundedIcon />,
                          Completed: <BlockIcon />,
                        }[row.status]
                      }
                      color={
                        {
                          New: "success",
                          In_progress: "neutral",
                          Completed: "primary",
                        }[row.status] as ColorPaletteProp
                      }
                    >
                      {row.status}
                    </Chip>
                    <RowMenu
                      onEdit={() => onEdit(row.id)}
                      onDelete={() => onDelete(row.id)}
                    />
                  </Box>
                </ListItem>
                <ListDivider />
              </List>
            ))}
            <Box
              className="Pagination"
              sx={{
                display: "flex",
                alignItems: "center",
                py: 2,
              }}
            >
              <IconButton
                aria-label="previous page"
                variant="outlined"
                color="neutral"
                size="sm"
              >
                <KeyboardArrowLeftIcon />
              </IconButton>
              <Typography level="body-sm" sx={{ mx: "auto" }}>
                Page 1 of 1
              </Typography>
              <IconButton
                aria-label="next page"
                variant="outlined"
                color="neutral"
                size="sm"
              >
                <KeyboardArrowRightIcon />
              </IconButton>
            </Box>
          </>
        )}
      </Box>
    </Box>
  );
}
