import { Box, FormControl, FormLabel, Select, Option, Input } from "@mui/joy";
import SearchIcon from "@mui/icons-material/Search";
import { useEffect, useState } from "react";

export interface Filters {
  status?: "NEW" | "IN_PROGRESS" | "COMPLETED";
  sort?: "createdAt" | "title";
  search?: string;
}

interface TaskFiltersProps {
  filters?: Filters; // текущие фильтры (контролируемые)
  onFilterChange?: (filters: Filters) => void;
}

export default function TaskFilters({
  filters: controlledFilters,
  onFilterChange,
}: TaskFiltersProps) {
  // локальное состояние, инициализируем из пропсов или пустое
  const [status, setStatus] = useState<Filters["status"]>(
    controlledFilters?.status
  );
  const [sortBy, setSortBy] = useState<Filters["sort"]>(
    controlledFilters?.sort
  );
  const [search, setSearch] = useState(controlledFilters?.search || "");

  // Синхронизируем локальный стейт при изменении controlledFilters извне
  useEffect(() => {
    if (controlledFilters) {
      setStatus(controlledFilters.status);
      setSortBy(controlledFilters.sort);
      setSearch(controlledFilters.search || "");
    }
  }, [controlledFilters]);

  // Дебаунс для поискового запроса
  useEffect(() => {
    const handler = setTimeout(() => {
      onFilterChange?.({
        status,
        sort: sortBy,
        search: search.trim() || undefined,
      });
    }, 300);

    return () => clearTimeout(handler);
  }, [status, sortBy, search, onFilterChange]);

  return (
    <Box
      sx={{
        borderRadius: "sm",
        py: 2,
        display: { xs: "none", sm: "flex" },
        flexWrap: "wrap",
        gap: 1.5,
        "& > *": { minWidth: { xs: "120px", md: "160px" } },
      }}
    >
      <FormControl sx={{ flex: 1 }} size="sm">
        <FormLabel>Search for task</FormLabel>
        <Input
          size="sm"
          placeholder="Search"
          startDecorator={<SearchIcon />}
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
      </FormControl>
      <FormControl size="sm">
        <FormLabel>Status</FormLabel>
        <Select
          value={status}
          onChange={(_, v) => setStatus((v as Filters["status"]) || undefined)}
          size="sm"
          placeholder="Filter by status"
        >
          <Option value="NEW">New</Option>
          <Option value="IN_PROGRESS">In progress</Option>
          <Option value="COMPLETED">Completed</Option>
        </Select>
      </FormControl>
      <FormControl size="sm">
        <FormLabel>Sort By</FormLabel>
        <Select
          value={sortBy}
          onChange={(_, v) => setSortBy((v as Filters["sort"]) || undefined)}
          size="sm"
          placeholder="Sort by"
        >
          <Option value="createdAt">Date</Option>
          <Option value="title">Title</Option>
        </Select>
      </FormControl>
    </Box>
  );
}
