import * as React from "react";
import {
  Box,
  Avatar,
  Chip,
  Typography,
  List,
  ListItem,
  ListItemContent,
  ListItemDecorator,
  ListDivider,
  IconButton,
  Menu,
  MenuButton,
  MenuItem,
  Dropdown,
  Divider,
  CircularProgress,
  Button,
} from "@mui/joy";
import { ColorPaletteProp } from "@mui/joy/styles";

import MoreHorizRoundedIcon from "@mui/icons-material/MoreHorizRounded";
import CheckRoundedIcon from "@mui/icons-material/CheckRounded";
import BlockIcon from "@mui/icons-material/Block";
import AutorenewRoundedIcon from "@mui/icons-material/AutorenewRounded";
import KeyboardArrowRightIcon from "@mui/icons-material/KeyboardArrowRight";
import KeyboardArrowLeftIcon from "@mui/icons-material/KeyboardArrowLeft";

import { getTasks, TaskResponse } from "../../api/task";
import TaskFilters, { Filters } from "./TaskFilters";

function RowMenu({
  onEdit,
  onDelete,
}: {
  onEdit?: () => void;
  onDelete?: () => void;
}) {
  return (
    <Dropdown>
      <MenuButton
        slots={{ root: IconButton }}
        slotProps={{ root: { variant: "plain", color: "neutral", size: "sm" } }}
      >
        <MoreHorizRoundedIcon />
      </MenuButton>
      <Menu size="sm" sx={{ minWidth: 140 }}>
        <MenuItem onClick={onEdit}>Edit</MenuItem>
        <Divider />
        <MenuItem color="danger" onClick={onDelete}>
          Delete
        </MenuItem>
      </Menu>
    </Dropdown>
  );
}

export function toQueryParams(filters: Filters): Record<string, string> {
  const params: Record<string, string> = {};

  if (filters.status) {
    // Normalize status to match backend expectations (adjust based on backend)
    params.status = filters.status; // Use as-is or transform (e.g., toLowerCase())
  }
  if (filters.sort) {
    params.sort = filters.sort;
  }
  // Add cache-busting parameter to avoid caching issues
  params._ = Date.now().toString();

  console.log("Query params:", params); // Debug: Log query parameters
  return params;
}

export default function TaskList() {
  const [tasks, setTasks] = React.useState<TaskResponse[]>([]);
  const [loading, setLoading] = React.useState(true);
  const [error, setError] = React.useState<string | null>(null);
  const [filters, setFilters] = React.useState<Filters>({ sort: "createdAt" });

  React.useEffect(() => {
    async function fetchData() {
      console.log("Fetching tasks with filters:", filters); // Debug: Log filters
      setLoading(true);
      setError(null);
      try {
        const params = toQueryParams(filters);
        const data = await getTasks(params);
        console.log("Fetched tasks:", data); // Debug: Log response
        // Ensure new array reference to trigger re-render
        setTasks([...data]);
      } catch (error) {
        console.error("Error fetching tasks:", error);
        setError("Failed to load tasks. Please try again.");
      } finally {
        setLoading(false);
      }
    }

    fetchData();
  }, [filters]);

  if (loading) {
    return (
      <Box sx={{ display: "flex", justifyContent: "center", mt: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Box sx={{ display: "flex", justifyContent: "center", mt: 4 }}>
        <Typography color="danger">{error}</Typography>
      </Box>
    );
  }

  return (
    <>
      <Box sx={{ display: "flex", justifyContent: "space-between", mb: 2 }}>
        <TaskFilters filters={filters} onFilterChange={setFilters} />
        <Button onClick={() => setFilters({})}>Reset Filters</Button>
      </Box>
      <Box sx={{ display: { xs: "block", sm: "none" }, px: 2 }}>
        {tasks.length === 0 ? (
          <Typography level="body-md">No tasks found.</Typography>
        ) : (
          tasks.map((task) => (
            <List key={task.id} size="sm" sx={{ "--ListItem-paddingX": 0 }}>
              <ListItem
                sx={{
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "start",
                  mb: 2,
                }}
              >
                <ListItemContent
                  sx={{ display: "flex", gap: 2, alignItems: "start" }}
                >
                  <ListItemDecorator>
                    <Avatar size="sm">{task.title[0]}</Avatar>
                  </ListItemDecorator>
                  <div>
                    <Typography gutterBottom sx={{ fontWeight: 600 }}>
                      {task.title}
                    </Typography>
                    <Typography
                      level="body-xs"
                      gutterBottom
                      noWrap
                      sx={{ maxWidth: 240 }}
                    >
                      {task.description}
                    </Typography>
                    <Box
                      sx={{
                        display: "flex",
                        alignItems: "center",
                        gap: 0.5,
                        mb: 1,
                        flexWrap: "wrap",
                      }}
                    >
                      <Typography level="body-xs">
                        Created: {new Date(task.createdAt).toLocaleDateString()}
                      </Typography>
                      <Typography level="body-xs">•</Typography>
                      <Typography level="body-xs">ID: {task.id}</Typography>
                    </Box>
                    <Typography level="body-xs">Until: {task.dueDate}</Typography>
                  </div>
                </ListItemContent>
                <Box
                  sx={{
                    display: "flex",
                    flexDirection: "column",
                    alignItems: "flex-end",
                    gap: 1,
                  }}
                >
                  <Chip
                    variant="soft"
                    size="sm"
                    startDecorator={
                      {
                        NEW: <CheckRoundedIcon />,
                        IN_PROGRESS: <AutorenewRoundedIcon />,
                        COMPLETED: <BlockIcon />,
                      }[task.status]
                    }
                    color={
                      {
                        NEW: "success",
                        IN_PROGRESS: "neutral",
                        COMPLETED: "primary",
                      }[task.status] as ColorPaletteProp
                    }
                  >
                    {task.status}
                  </Chip>
                  <RowMenu />
                </Box>
              </ListItem>
              <ListDivider />
            </List>
          ))
        )}
        <Box
          className="Pagination-mobile"
          sx={{
            display: { xs: "flex", md: "none" },
            alignItems: "center",
            py: 2,
          }}
        >
          <IconButton
            aria-label="previous page"
            variant="outlined"
            color="neutral"
            size="sm"
          >
            <KeyboardArrowLeftIcon />
          </IconButton>
          <Typography level="body-sm" sx={{ mx: "auto" }}>
            Page 1 of 1
          </Typography>
          <IconButton
            aria-label="next page"
            variant="outlined"
            color="neutral"
            size="sm"
          >
            <KeyboardArrowRightIcon />
          </IconButton>
        </Box>
      </Box>
    </>
  );
}
